import React from 'react';
import { PersonalInfo } from '../../types';
import './Header.css';

interface HeaderProps {
  personalInfo: PersonalInfo;
}

const Header: React.FC<HeaderProps> = ({ personalInfo }) => {
  return (
    <header className="header">
      <div className="container">
        <ul className="social-icons pt-3">
          {personalInfo.socialLinks.map((link) => (
            <li key={link.platform} className="social-item">
              <a 
                className="social-link text-light" 
                href={link.url} 
                target="_blank" 
                rel="noopener noreferrer"
                aria-label={link.title}
              >
                <i className={link.icon} aria-hidden="true" title={link.title}></i>
              </a>
            </li>
          ))}
        </ul>

        <div className="header-content">
          <h4 className="header-subtitle">Hello, I am</h4>
          <h1 className="header-title">{personalInfo.name}</h1>
          <h6 className="header-mono mt-5">{personalInfo.title}</h6>
          <a 
            href="/resume/Saqib_Zafar_Resume.pdf" 
            className="btn btn-primary bg-t-red btn-rounded" 
            target="_blank"
            download="Saqib_Zafar_Resume.pdf"
          >
            <i className="fa fa-download" aria-hidden="true"></i>
            <span className="ml-2">Download My Resume</span>
          </a>
        </div>
      </div>
    </header>
  );
};

export default Header;
